CustomField.formula:
IF( ISPICKVAL( tth_Booking_Type__c ,'Flight') , 'You are on flight ' +  tth_Flight_Number__c + ' departing at ' +  tth_Time__c + ' sitting in ' +  TEXT(tth_Travel_Class__c) + ' in seat ' +  tth_Allocated_Seat__c + ' and your reservation number is ' +  tth_Record_Locator__c ,
   IF( ISPICKVAL( tth_Booking_Type__c ,'Car') , 'You are picking up your car from ' +  tth_Location__c + ' at ' +  tth_Time__c + ' and your reservation number is ' + tth_Booking_Confirmation__c,
      IF( ISPICKVAL( tth_Booking_Type__c ,'Hotel') || ISPICKVAL( tth_Booking_Type__c ,'Accomodation') , 'You are staying at ' + tth_Location__c + ' in a ' +  TEXT(tth_Room_Type__c) + ' room and your reservation number is ' + tth_Booking_Confirmation__c, 
         IF( ISPICKVAL( tth_Booking_Type__c ,'Rail') || ISPICKVAL( tth_Booking_Type__c ,'Bus') || ISPICKVAL( tth_Booking_Type__c ,'Ferry') || ISPICKVAL( tth_Booking_Type__c ,'Transport'), 'You are on ' + tth_Flight_Number__c + ' departing from ' +  tth_Location__c + ' at ' +  tth_Time__c + ' in seat ' +  tth_Allocated_Seat__c + ' on ticket number ' +  tth_Booking_Confirmation__c , '')
      )
   )
)
